mapper.select:
select 

from tab_user User
where User.ID = #{id,jdbcType=INTEGER}

mapper.insert:
insert into tab_user (PHONE_NUMBER, PASSWORD, USERNAME
  )
values (#{phoneNumber,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{username,jdbcType=VARCHAR}
  )

mapper.update:
update tab_user
set PHONE_NUMBER = #{phoneNumber,jdbcType=VARCHAR},
  PASSWORD = #{password,jdbcType=VARCHAR},
  USERNAME = #{username,jdbcType=VARCHAR}
where ID = #{id,jdbcType=INTEGER}
